svg.script:


var sx = 10;
var sy = 10;

nx = 5;
sep = 20;
rad = 40;
rea = 1/2;
h = 75;

k = 1;

var draw = SVG('drawing');
draw.line(sx, sx+h+rad*rea, sx + nx*( rad+ sep), sx+h+rad*rea).stroke({ color: '#51A0D5', width: 1 })

for (let i = 0; i < nx; i++){
    draw.line(sx+sep/2 + i*(sep+rad)/2, sx+h+rad*rea)
}
